import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { first, finalize, map } from 'rxjs/operators';
import { AccountService } from '@app/_services/account.service';
import { AlertService } from '@app/_services/alert.service';
import { FormBuilder, FormGroup, Validators, AbstractControl } from '@angular/forms';

@Component({
    templateUrl: 'add-edit.component.html'
})
export class AddEditEmployeeComponent implements OnInit {
    form!: FormGroup;
    employee: any = {
        employeeId: '',
        userId: null,
        position: '',
        departmentId: null,
        hireDate: null,
        status: 'Active'
    };
    users: any[] = [];
    availableUsers: any[] = []; // Users not already assigned to employees
    departments: any[] = [];
    id: string;
    isAddMode: boolean;
    errorMessage: string = '';
    loading = false;
    submitted = false;
    existingEmployees: any[] = []; // To keep track of existing employees for validation

    constructor(
        private formBuilder: FormBuilder,
        private route: ActivatedRoute,
        private router: Router,
        private accountService: AccountService,
        private alertService: AlertService
    ) { }

    ngOnInit() {
        this.id = this.route.snapshot.params['id'];
        this.isAddMode = !this.id;

        // Initialize the form with validation
        this.form = this.formBuilder.group({
            employeeId: ['', Validators.required],
            userId: [null, Validators.required],
            position: ['', Validators.required],
            departmentId: [null, Validators.required],
            hireDate: [null, Validators.required],
            status: ['Active', Validators.required]
        });

        // Load existing employees first, then users and departments
        this.loadExistingEmployees();

        if (this.isAddMode) {
            // Fetch and pre-fill the next employeeId
            this.accountService.getNextEmployeeId()
                .pipe(first())
                .subscribe({
                    next: (res) => {
                        this.form.patchValue({ employeeId: res.employeeId });
                    },
                    error: (error) => {
                        this.errorMessage = 'Failed to fetch next Employee ID';
                    }
                });
        } else {
            this.loadEmployee();
        }
    }

    // Convenience getter for easy access to form fields
    get f(): { [key: string]: AbstractControl } { return this.form.controls; }

    loadExistingEmployees() {
        this.loading = true;
        this.accountService.getAllEmployees()
            .pipe(
                first(),
                finalize(() => {
                    // After loading employees, load users and departments
                    this.loadUsers();
                    this.loadDepartments();
                    this.loading = false;
                })
            )
            .subscribe({
                next: (employees) => {
                    this.existingEmployees = employees;
                },
                error: (error) => {
                    console.error('Error loading employees:', error);
                    this.errorMessage = 'Error loading employees data';
                }
            });
    }

    loadUsers() {
        this.accountService.getAllUsers()
            .pipe(first())
            .subscribe({
                next: (users) => {
                    console.log('Loaded users:', users);
                    if (Array.isArray(users)) {
                        this.users = users;

                        // Filter out users that are already assigned to employees
                        this.availableUsers = this.users.filter(user => {
                            // If we're in edit mode, allow the current user to be selected
                            if (!this.isAddMode && this.id) {
                                const currentEmployee = this.existingEmployees.find(emp => emp.id === Number(this.id));
                                if (currentEmployee && currentEmployee.userId === user.id) {
                                    return true;
                                }
                            }

                            // Check if the user is already assigned to an employee
                            return !this.existingEmployees.some(emp => emp.userId === user.id);
                        });
                    } else {
                        console.error('Expected users to be an array but got:', typeof users);
                        this.errorMessage = 'Error loading users data';
                        this.users = [];
                        this.availableUsers = [];
                    }
                },
                error: (error) => {
                    console.error('Error loading users:', error);
                    this.errorMessage = 'Error loading users data';
                }
            });
    }

    loadDepartments() {
        this.accountService.getAllDepartments()
            .pipe(first())
            .subscribe(departments => this.departments = departments);
    }

    loadEmployee() {
        this.loading = true;
        this.accountService.getEmployeeById(this.id)
            .pipe(first())
            .subscribe({
                next: (employee) => {
                    this.form.patchValue(employee);
                    this.loading = false;
                },
                error: (error) => {
                    this.errorMessage = error;
                    this.loading = false;
                }
            });
    }

    save() {
        this.submitted = true;
        this.alertService.clear();

        // Stop here if form is invalid
        if (this.form.invalid) {
            return;
        }

        // Check if selected user is already assigned to another employee (for add mode or when changing user)
        const selectedUserId = this.form.value.userId;
        const existingEmployeeWithUser = this.existingEmployees.find(emp => 
            emp.userId === selectedUserId && 
            (!this.id || Number(this.id) !== emp.id) // Exclude current employee in edit mode
        );

        if (existingEmployeeWithUser) {
            this.errorMessage = `This user is already assigned to employee ${existingEmployeeWithUser.employeeId}`;
            return;
        }

        if (this.loading) {
            return;
        }

        this.loading = true;
        if (this.isAddMode) {
            this.createEmployee();
        } else {
            this.updateEmployee();
        }
    }

    private createEmployee() {
        this.accountService.createEmployee(this.form.value)
            .pipe(first())
            .subscribe({
                next: () => {
                    this.alertService.success('Employee created successfully', { keepAfterRouteChange: true });
                    this.router.navigate(['../'], { relativeTo: this.route });
                },
                error: (error) => {
                    this.errorMessage = error;
                    this.loading = false;
                }
            });
    }

    private updateEmployee() {
        this.accountService.updateEmployee(this.id, this.form.value)
            .pipe(first())
            .subscribe({
                next: () => {
                    this.alertService.success('Employee updated successfully', { keepAfterRouteChange: true });
                    this.router.navigate(['../../'], { relativeTo: this.route });
                },
                error: (error) => {
                    this.errorMessage = error;
                    this.loading = false;
                }
            });
    }

    cancel() {
        if (this.isAddMode) {
            this.router.navigate(['../'], { relativeTo: this.route });
        } else {
            this.router.navigate(['../../'], { relativeTo: this.route });
        }
    }
}
