import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { first } from 'rxjs/operators';

import { EmployeeService, DepartmentService } from '@app/_services';

@Component({ templateUrl: 'transfer.component.html' })
export class TransferComponent implements OnInit {
    employee: any;
    departments = [];
    departmentId: number;

    constructor(
        private router: Router,
        private route: ActivatedRoute,
        private employeeService: EmployeeService,
        private departmentService: DepartmentService
    ) {}

    ngOnInit() {
        const id = this.route.snapshot.params['id'];
        this.loadEmployee(id);
        this.loadDepartments();
    }

    loadEmployee(id: string) {
        this.employeeService.getById(id)
            .pipe(first())
            .subscribe({
                next: (employee) => {
                    this.employee = employee;
                    this.departmentId = employee.departmentId;
                },
                error: (error) => console.error('Error loading employee:', error)
            });
    }

    loadDepartments() {
        this.departmentService.getAll()
            .pipe(first())
            .subscribe({
                next: (departments) => this.departments = departments,
                error: (error) => console.error('Error loading departments:', error)
            });
    }

    transfer() {
        this.employeeService.transfer(this.employee.id.toString(), this.departmentId)
            .pipe(first())
            .subscribe({
                next: () => this.router.navigate(['/employees']),
                error: (error) => console.error('Error transferring employee:', error)
            });
    }

    cancel() {
        this.router.navigate(['/employees']);
    }
}
