import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';

import { SubNavComponent } from './subnav.component';
import { LayoutComponent } from './layout.component';
import { OverviewComponent } from './overview.component';

// Lazy loading function for Accounts (existing)
const accountsModule = () => import('./accounts/accounts.module').then(x => x.AccountsModule);

// Lazy loading functions for new modules
const departmentsModule = () => import('./departments/departments.module').then(x => x.DepartmentsModule);
const employeesModule = () => import('./employees/employees.module').then(x => x.EmployeesModule);
const profileModule = () => import('./profile/profile.module').then(x => x.ProfileModule);
const requestsModule = () => import('./requests/requests.module').then(x => x.RequestsModule);

const routes: Routes = [
    {
        path: '',
        component: SubNavComponent,
        outlet: 'subnav' // This will render SubNavComponent in a <router-outlet name="subnav"></router-outlet>
    },
    {
        path: '', // This is the base path for the admin section (e.g., /admin)
        component: LayoutComponent, // The main layout for the admin section
        children: [
            {
                path: '', // Default child route for /admin, renders OverviewComponent
                component: OverviewComponent
            },
            {
                path: 'accounts', // Route for /admin/accounts
                loadChildren: accountsModule
            },
            {
                path: 'departments', // Route for /admin/departments
                loadChildren: departmentsModule
            },
            {
                path: 'employees', // Route for /admin/employees
                loadChildren: employeesModule
            },
            {
                path: 'profile', // Route for /admin/profile
                loadChildren: profileModule
            },
            {
                path: 'requests', // Route for /admin/requests
                loadChildren: requestsModule
            }
            // Add other top-level admin sections here
        ]
    }
];

@NgModule({
    imports: [RouterModule.forChild(routes)],
    exports: [RouterModule]
})
export class AdminRoutingModule { }
